mapper.select:
select count(*)
from sys_dept as dept left join merch_info as merch on merch.dept_id= dept.dept_id
where dept.del_flag=0

   and dept.parent_id =#{parentId}


    and dept.dept_type = #{deptType}



    and (
    merch.merchno =#{keyword}
    or dept.name like concat('%',#{keyword},'%')
    or dept.legal_name like concat('%',#{keyword},'%'))

select dept.dept_id as Id,dept.*,merch.id as merch_id,merch.merchno,merchno_sub
    from sys_dept as dept left join merch_info as merch on merch.dept_id= dept.dept_id
    where dept.del_flag=0


    and dept.parent_id = #{parentId}


    and dept.dept_type = #{deptType}



    and (
    merch.merchno =#{keyword}
    or dept.name like concat('%',#{keyword},'%')
    or dept.legal_name like concat('%',#{keyword},'%'))


  order by dept.dept_id  desc


    limit #{offset}, #{limit}

select dept.dept_id as Id,dept.*,merch.id as merch_id,merch.merchno,merchno_sub
  from sys_dept as dept left join merch_info as merch on merch.dept_id= dept.dept_id
  where dept.dept_id=#{deptId}
