mapper.select:
select t.*, m.merch_name AS merchName from trans_data t
LEFT JOIN merch_info m ON m.merchno = t.merchantId
WHERE
m.enabled = 1

	and m.merch_name like concat('%',#{merchName},'%')


	and m.user_id = #{userId}


	and m.user_id  IN

		#{userId}



	and t.txn_dateTime >= #{dateStart}


	and t.txn_dateTime <= #{dateEnd}


	and t.merchantId = #{merchno}

order by t.id asc

	limit #{offset}, #{limit}

select COUNT(t.id) from trans_data t
LEFT JOIN merch_info m ON m.merchno = t.merchantId
WHERE  m.enabled = 1

	and m.merch_name like concat('%',#{merchName},'%')


	and m.user_id  IN

		#{userId}



	and t.txn_dateTime >= #{dateStart}


	and t.txn_dateTime <= #{dateEnd}


	and t.merchantId = #{merchno}

select t.* from trans_data t
LEFT JOIN merch_info m ON m.merchno = t.merchantId
WHERE m.enabled = 1
AND t.txn_dateTime >= #{date}

	and m.user_id  IN

		#{userId}

select t.cardType AS cardType ,sum(t.amt) AS amt
from trans_data t
LEFT JOIN merch_info m ON m.merchno = t.merchantId
WHERE m.enabled = 1

	and m.merch_name like concat('%',#{merchName},'%')


	and m.user_id  IN

		#{userId}



	and t.txn_dateTime >= #{dateStart}


	and t.txn_dateTime <= #{dateEnd}

GROUP BY t.cardType
